mapper.delete:

delete from DB_COM_CODE
where CLASS_CODE = #{classCode,jdbcType=VARCHAR}
  and COM_CODE = #{comCode,jdbcType=VARCHAR}

mapper.select:

select CLASS_CODE, COM_CODE, COM_NAME, USE_YN, REMARKS, REG_USER_ID, REG_DTM, MOD_USER_ID, 
MOD_DTM, BKCK_FG
from DB_COM_CODE
where CLASS_CODE = #{classCode,jdbcType=VARCHAR}
  and COM_CODE = #{comCode,jdbcType=VARCHAR}
