mapper.delete:

delete from DB_COM_EQUCOM
where EQU_COM_EQUIP = #{equComEquip,jdbcType=VARCHAR}
  and EQU_COM_EQNO = #{equComEqno,jdbcType=VARCHAR}
  and EQU_COM_KIND = #{equComKind,jdbcType=VARCHAR}

mapper.update:

update DB_COM_EQUCOM
set EQU_COM_DATE = #{equComDate,jdbcType=TIMESTAMP},
  EQU_COM_DATA = #{equComData,jdbcType=VARCHAR},
  REG_USER_ID = #{regUserId,jdbcType=VARCHAR},
  REG_DTM = #{regDtm,jdbcType=TIMESTAMP},
  MOD_USER_ID = #{modUserId,jdbcType=VARCHAR},
  MOD_DTM = #{modDtm,jdbcType=TIMESTAMP},
  BKCK_FG = #{bkckFg,jdbcType=VARCHAR}
where EQU_COM_EQUIP = #{equComEquip,jdbcType=VARCHAR}
  and EQU_COM_EQNO = #{equComEqno,jdbcType=VARCHAR}
  and EQU_COM_KIND = #{equComKind,jdbcType=VARCHAR}

mapper.select:

select EQU_COM_EQUIP, EQU_COM_EQNO, EQU_COM_KIND, EQU_COM_DATE, EQU_COM_DATA, REG_USER_ID, 
REG_DTM, MOD_USER_ID, MOD_DTM, BKCK_FG
from DB_COM_EQUCOM
where EQU_COM_EQUIP = #{equComEquip,jdbcType=VARCHAR}
  and EQU_COM_EQNO = #{equComEqno,jdbcType=VARCHAR}
  and EQU_COM_KIND = #{equComKind,jdbcType=VARCHAR}
