mapper.delete:

delete from DB_COM_USRAUT
where UA_USID = #{uaUsid,jdbcType=VARCHAR}
  and UA_MNID = #{uaMnid,jdbcType=VARCHAR}

mapper.update:

update DB_COM_USRAUT
set REMARKS = #{remarks,jdbcType=VARCHAR},
  REG_USER_ID = #{regUserId,jdbcType=VARCHAR},
  REG_DTM = #{regDtm,jdbcType=TIMESTAMP},
  MOD_USER_ID = #{modUserId,jdbcType=VARCHAR},
  MOD_DTM = #{modDtm,jdbcType=TIMESTAMP},
  BKCK_FG = #{bkckFg,jdbcType=VARCHAR}
where UA_USID = #{uaUsid,jdbcType=VARCHAR}
  and UA_MNID = #{uaMnid,jdbcType=VARCHAR}

mapper.select:

select UA_USID, UA_MNID, REMARKS, REG_USER_ID, REG_DTM, MOD_USER_ID, MOD_DTM, BKCK_FG
from DB_COM_USRAUT
where UA_USID = #{uaUsid,jdbcType=VARCHAR}
  and UA_MNID = #{uaMnid,jdbcType=VARCHAR}
