mapper.delete:

delete from DB_SYS_CLCHST
where HST_DTM = #{hstDtm,jdbcType=TIMESTAMP}
  and TASKKY = #{taskky,jdbcType=VARCHAR}
  and SKUKEY = #{skukey,jdbcType=VARCHAR}

mapper.update:

update DB_SYS_CLCHST
set OWNRKY = #{ownrky,jdbcType=VARCHAR},
  DESC01 = #{desc01,jdbcType=VARCHAR},
  LOCASR = #{locasr,jdbcType=VARCHAR},
  LOCATG = #{locatg,jdbcType=VARCHAR},
  QTTAOR = #{qttaor,jdbcType=NUMERIC},
  QTCOMP = #{qtcomp,jdbcType=NUMERIC},
  CMD_DIV = #{cmdDiv,jdbcType=VARCHAR}
where HST_DTM = #{hstDtm,jdbcType=TIMESTAMP}
  and TASKKY = #{taskky,jdbcType=VARCHAR}
  and SKUKEY = #{skukey,jdbcType=VARCHAR}

mapper.select:

select HST_DTM, TASKKY, SKUKEY, OWNRKY, DESC01, LOCASR, LOCATG, QTTAOR, QTCOMP, CMD_DIV
from DB_SYS_CLCHST
where HST_DTM = #{hstDtm,jdbcType=TIMESTAMP}
  and TASKKY = #{taskky,jdbcType=VARCHAR}
  and SKUKEY = #{skukey,jdbcType=VARCHAR}
