mapper.delete:

delete from DB_SYS_ERRLOG
where LOG_DTM = #{logDtm,jdbcType=TIMESTAMP}
  and LOG_SEQ = #{logSeq,jdbcType=NUMERIC}

mapper.select:

select LOG_DTM, LOG_SEQ, ERR_EQUIP, ERR_EQ_NO, ERR_CODE, ERR_MSG, ERR_POS, ERR_STON, 
JOB_NO, LOCT, PLT, PLT_ID, REMARKS, REG_USER_ID
from DB_SYS_ERRLOG
where LOG_DTM = #{logDtm,jdbcType=TIMESTAMP}
  and LOG_SEQ = #{logSeq,jdbcType=NUMERIC}
