mapper.delete:

delete from DB_SYS_SYSLOG
where USER_ID = #{userId,jdbcType=VARCHAR}
  and CONN_DTM = #{connDtm,jdbcType=TIMESTAMP}

mapper.update:

update DB_SYS_SYSLOG
set USER_NAME = #{userName,jdbcType=VARCHAR},
  END_DTM = #{endDtm,jdbcType=TIMESTAMP},
  CP_IP = #{cpIp,jdbcType=VARCHAR},
  CP_NAME = #{cpName,jdbcType=VARCHAR},
  CONN_LOG = #{connLog,jdbcType=VARCHAR}
where USER_ID = #{userId,jdbcType=VARCHAR}
  and CONN_DTM = #{connDtm,jdbcType=TIMESTAMP}

mapper.select:

select USER_ID, CONN_DTM, USER_NAME, END_DTM, CP_IP, CP_NAME, CONN_LOG
from DB_SYS_SYSLOG
where USER_ID = #{userId,jdbcType=VARCHAR}
  and CONN_DTM = #{connDtm,jdbcType=TIMESTAMP}
